<th class='theadtr'>#</th>            <th style="width: 26%" class="text-left theadtr">Columns</th>            <th class='theadtr' style="width: 26%">Column Title</th>            <th class='theadtr'>Width</th>            <th class='theadtr'>Background Color</th>            <th class='theadtr'>Font Color</th>            <th class='theadtr'>Sort Order</th>        </tr>        </thead>        <tbody>        <?php        $num = 1;        if (!empty($checklistDetail)) {            foreach ($checklistDetail as $val) { ?>                <tr>                    <td class="text-right" style="width: 5%;"><?php echo $num?></td>                    <td class="text-left"> <?php echo form_dropdown('columns[]', array(''=>'Select Column Type','1'=>'Label','2'=>'Textbox','3'=>'Textarea','4'=>'Radio Button','5'=>'Checkbox'),$val['columnTypeID'], 'class="form-control select2" id="columns" '); ?></td>                    <td> <input type="text" class="form-control " id="columntitle" name="columntitle[]" value="<?php echo $val['detailDescription']?>"></td>                    <td style="width: 8%;"> <input type="text" class="form-control " id="Width" name="Width[]" value="<?php echo $val['width']?>"></td>                    <td style="text-align: center;width: 11%;padding-left: 4%"><div id="bgcolor" name="bgcolor[]" style="width: 5%;" class="input-group colorpicker-component bgcolor">                            <input type="hidden" readonly id="bgcolor" name="bgcolor[]" value="<?php echo $val['bgColor']?>"                                   class="form-control bgcolor"/>                            <span class="input-group-addon"><i style="background-color: <?php echo $val['bgColor']?>"></i></span>                        </div></td>                    <td style="text-align: center;width: 11%;padding-left: 4%"><div  id="fontcolor" name="fontcolor[]" style="width: 5%;" class="input-group colorpicker-component fontcolor">                            <input type="hidden" 